import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';

@Injectable({
  providedIn: 'root',
})
export class AuthService {
  private baseUrl = 'http://localhost:3000';
  private token!: string;

  constructor(private http: HttpClient) {}

  getAddress(pincode: string): Observable<any> {
    const url = `http://www.postalpincode.in/api/pincode/${pincode}`;
    return this.http.get<any>(url);
  }

  addAddress(address: any): Observable<any> {
    const url = `${this.baseUrl}/address/add`;
    return this.http.post<any>(url, address);
  }

  editAddress(address: any): Observable<any> {
    const url = `${this.baseUrl}/address/edit`;
    return this.http.put<any>(url, address);
  }

  getProfile(): Observable<any> {
    const url = `${this.baseUrl}/user/profile`;
    return this.http.get<any>(url);
  }

  setToken(token: string): void {
    this.token = token;
  }

  getToken(): string {
    return this.token;
  }
}
