import { Component, Input, Output, EventEmitter } from '@angular/core';
import { FormGroup, FormControl } from '@angular/forms';

@Component({
  selector: 'app-edit-address',
  templateUrl: './edit-address.component.html',
  styleUrls: ['./edit-address.component.css'],
})
export class EditAddressComponent {
  @Input() address: any;
  @Output() addressUpdated = new EventEmitter<any>();

  addressForm = new FormGroup({
    addressLine1: new FormControl(''),
    city: new FormControl(''),
    state: new FormControl(''),
    postalCode: new FormControl(''),
  });

  constructor() {}

  ngOnChanges() {
    if (this.address) {
      this.addressForm.patchValue({
        addressLine1: this.address.addressLine1,
        city: this.address.city,
        state: this.address.state,
        postalCode: this.address.postalCode,
      });
    }
  }

  onSubmit() {
    const updatedAddress = {
      ...this.address,
      ...this.addressForm.value,
    };
    this.addressUpdated.emit(updatedAddress);
  }

  cancel() {
    this.addressUpdated.emit(this.address);
  }
}
<div class="card p-3 shadow">
  <form>
    <div class="form-group">
      <label for="addressLine1">Address</label>
      <input type="text" class="form-control" id="addressLine1" [(ngModel)]="address.addressLine1" name="addressLine1" />
    </div>
    <div class="form-group">
      <label for="city">City</label>
      <input type="text" class="form-control" id="city" [(ngModel)]="address.city" name="city" />
    </div>
    <div class="form-group">
      <label for="state">State</label>
      <input type="text" class="form-control" id="state" [(ngModel)]="address.state" name="state" />
    </div>
    <div class="form-group">
      <label for="postalCode">Postal Code</label>
      <input type="text" class="form-control" id="postalCode" [(ngModel)]="address.postalCode" name="postalCode" />
    </div>
    <button class="btn btn-primary" (click)="onSubmit()">Save</button>
    <button class="btn btn-secondary" (click)="cancel()">Cancel</button>
  </form>
</div>
