import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService } from 'src/app/service/auth.service';
import { Address } from 'src/app/models/address.model';

@Component({
  selector: 'app-profile',
  templateUrl: './profile.component.html',
  styleUrls: ['./profile.component.css'],
})
export class ProfileComponent implements OnInit {
  data: any;
  address: Address = new Address();
  editMode: boolean = false;

  constructor(private auth: AuthService, private router: Router) {}

  ngOnInit(): void {
    this.getProfile();
  }

  getProfile() {
    this.auth.getProfile().subscribe(
      (res: any) => {
        console.log(res);
        if (res.success) {
          this.data = res.data;
          if (this.data.address) {
            this.address = this.data.address;
          }
        } else {
          // handle error
        }
      },
      (err) => {
        console.log(err);
      }
    );
  }

  logout() {
    localStorage.clear();
    this.router.navigate(['/dashboard']);
  }

  addAddress() {
    this.auth.addAddress(this.address).subscribe(
      (res: any) => {
        if (res.success) {
          this.getProfile();
        } else {
          // handle error
        }
      },
      (err) => {
        console.log(err);
      }
    );
  }

  editAddress() {
    this.auth.editAddress(this.address).subscribe(
      (res: any) => {
        if (res.success) {
          this.getProfile();
          this.editMode = false;
        } else {
          // handle error
        }
      },
      (err) => {
        console.log(err);
      }
    );
  }
}
<div class="container">
  <div class="row justify-content-center mt-5">
    <div class="col-md-6">
      <div class="card mt-5 p-3 shadow">
        <ng-container *ngIf="data">
          <h2>Hello: {{ data.displayName }}</h2>
          <p>Email: {{ data.email }}</p>
          <div *ngIf="data.address">
            <h3>Address:</h3>
            <p>{{ data.address.line1 }}</p>
            <p>{{ data.address.line2 }}</p>
            <p>{{ data.address.city }}, {{ data.address.state }}</p>
            <p>{{ data.address.zip }}</p>
            <button class="btn btn-primary" (click)="editAddress()">
              Edit Address
            </button>
          </div>
          <div *ngIf="!data.address">
            <button class="btn btn-primary" (click)="addAddress()">
              Add Address
            </button>
          </div>
        </ng-container>
        <button class="btn btn-danger" (click)="logout()">Logout</button>
      </div>
    </div>
  </div>
</div>
