import { Component, OnInit } from '@angular/core';
import { AuthService } from 'src/app/service/auth.service';
import { Router } from '@angular/router';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-profile',
  templateUrl: './profile.component.html',
  styleUrls: ['./profile.component.css']
})
export class ProfileComponent implements OnInit {
  user: any;
  data: any;
  editMode = false;
  profileForm: FormGroup;

  constructor(
    private auth: AuthService,
    private router: Router,
    private fb: FormBuilder
  ) {
    this.profileForm = this.fb.group({
      displayName: ['', Validators.required],
      email: ['', Validators.required],
      addressLine1: ['', Validators.required],
      city: ['', Validators.required],
      state: ['', Validators.required],
      postalCode: ['', Validators.required]
    });
  }

  ngOnInit(): void {
    this.auth.getProfile().subscribe(
      (res: any) => {
        this.data = res.data;
        this.profileForm.patchValue(this.data);
      },
      (err: any) => {
        console.log(err);
      }
    );
  }

  logout(): void {
    this.auth.logout();
    this.router.navigate(['/dashboard']);
}


  editAddress() {
    this.editMode = true;
  }

  cancelEdit() {
    this.editMode = false;
  }
  onAddressUpdated() {
    this.editMode = false;
    window.location.reload();
  }
  updateAddress() {
    const address = this.profileForm.value;
    this.auth.updateAddress(address).subscribe(
      (res: any) => {
        this.data = res.data;
        this.editMode = false;
      },
      (err: any) => {
        console.log(err);
      }
    );
  }
}
<div class="container">
  <div class="row justify-content-center mt-5">
    <div class="col-md-6">
      <div class="card mt-5 p-3 shadow">
        <ng-container *ngIf="data">

          <h2>Hello: {{ data.displayName }}</h2>
          <p>Email: {{ data.email }}</p>
          <p>Address: {{data.addressLine1}}</p>
           <p>City: {{data.city}}</p>
          <p>State: {{data.state}}</p>
          <p>PostalCode: {{data.postalCode}}</p>

        <button class="btn btn-danger" (click)="logout()">Logout</button>


        <div *ngIf="!editMode">
          <p>Address: {{address.addressLine1}}</p>
          <p>City: {{address.city}}</p>
          <p>State: {{address.state}}</p>
          <p>PostalCode: {{address.postalCode}}</p>

          <button class="btn btn-primary" (click)="editAddress()">Edit</button>
        </div>
      </ng-container>

      </div>
    </div>
  </div>
</div>
