import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { AuthService } from 'src/app/service/auth.service';
import { HttpClient } from '@angular/common/http';


@Component({
  selector: 'app-edit-address',
  templateUrl: './edit-address.component.html',
  styleUrls: ['./edit-address.component.css']
})
export class EditAddressComponent implements OnInit {
  EditAddressForm: FormGroup;
  address: any;

  constructor(
    private fb: FormBuilder,
    private auth: AuthService,
    private router: Router,
    private http: HttpClient
  ) {
    this.EditAddressForm = this.fb.group({
      addressLine1: ['', Validators.required],
      city: ['', Validators.required],
      state: ['', Validators.required],
      postalCode: ['', Validators.required],
    });

    // if (this.EditAddressForm) {
    //   this.EditAddressForm.patchValue(this.address);
    // }
 this.auth.getAddress().subscribe(
      (res) => {
        this.address = res.data;
        this.EditAddressForm.patchValue(this.address);
      },
      (err) => {
        console.log(err);
      }
    );

    this.EditAddressForm?.get('postalCode')?.valueChanges.subscribe((postalCode: string) => {
      if (postalCode.length === 6) {
        this.http.get<any>(`https://api.postalpincode.in/pincode/${postalCode}`).subscribe(res => {
          if (res[0].Status === "Success") {
            const postOffice = res[0].PostOffice[0];

            this.address = {
              addressLine1: postOffice.Name,
              city: postOffice.District,
              state: postOffice.State,
              postalCode: postalCode
            };
            this.EditAddressForm.patchValue(this.address);
          }
        });
      }
    });
  }

  ngOnInit(): void {

    this.EditAddressForm.patchValue(this.address);
  }

// onSubmit() {
//   this.auth.updateAddress(this.address);
//   this.router.navigate(['/profile']);
// }
onSubmit() {
  this.auth.updateAddress(this.address).subscribe(
    (res) => {
      console.log(res);
      this.router.navigate(['/profile']); // navigate to profile route
    },
    (err) => {
      console.log(err);
    }
  );
}


}








<div class="container mt-2">
  <div class="row-justify-content-center">
    <div class="col-md-8">
      <div class="card shadow-lg p-4 mt-5 mb-6">

        <form [formGroup] ="EditAddressForm" (ngSubmit)="onSubmit()" class="text-left">
        <div class="form-group">

          <label>
            Address line 1
          </label>
          <input type="text" class="form-control" placeholder="Address line 1" formControlName="addressLine1">

          <label>
            City
          </label>
          <input type="text" class="form-control" placeholder="City" formControlName="city">

          <label>
            State
          </label>
          <input type="text" class="form-control" placeholder="State" formControlName="state">

          <label>
            Postal pincode
          </label>
          <input type="text" class="form-control" placeholder="Postal pincode" formControlName="postalCode">

        </div><br>
        <!-- <button class="btn btn-primary">Submit</button> -->
        <button class="btn btn-primary" (click)="onSubmit()">Save</button>

      </form>
    </div>
  </div>

</div>
