import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { environment } from '../../environment/environment';

@Injectable({
  providedIn: 'root',
})
export class AuthService {

  address: any;
  constructor(private http: HttpClient) {}

  signup(data: any): Observable<any> {
    return this.http.post(`${environment.URL}/auth/register`, data);
  }

  signin(data: any): Observable<any> {
    return this.http.post(`${environment.URL}/auth/login`, data);
  }

  getProfile(): Observable<any> {
    const headers = {
      Authorization: 'Bearer ' + localStorage.getItem('token'),
    };
    return this.http.get(`${environment.URL}/auth/profile`, {
      headers: headers,
    });
  }


  updateAddress(address: any) {
    const headers = {
      Authorization: 'Bearer ' + localStorage.getItem('token'),
    };
    this.http.put(`${environment.URL}/auth/address`, address, {
      headers: headers
    }).subscribe(
      (res: any) => {
        console.log('Address updated successfully');
        // Update the address variable
        this.address = address;
      },
      (err: any) => {
        console.log('Error updating address:', err);
      }
    );
  }


  getAddress(): Observable<any> {
    const headers = {
      Authorization: 'Bearer ' + localStorage.getItem('token'),
    };
    return this.http.get(`${environment.URL}/auth/address`, {
      headers: headers,
    });
  }


}
