import { Component,  OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { AuthService } from 'src/app/service/auth.service';


@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']

})
export class RegisterComponent implements OnInit{
  signupForm!: FormGroup;
  message:string = '';
  className = 'd-none'
  isProcess:boolean = false;

  constructor (private fb:FormBuilder, private auth:AuthService ) {
    this.signupForm = this.fb.group({
      'displayName':['', Validators.required],
      'email':['', Validators.required],
      'password':['', Validators.required]
    })
  }

  ngOnInit(): void {

  }
  signup() {
    const data = this.signupForm.value;
    delete data['confirm'];
    this.auth.signup(data).subscribe(res=>
      {
        if(res.success){
          this.isProcess = false;
          this.message = "Account has been created";
          this.className = 'alert alert-success';
        }else{
          this.isProcess = false;
          this.message = res.message;
          this.className= 'alert alert-danger';
        }
        // this.signupForm.reset();
      }, err => {
        this.isProcess = false;
          this.message = "Server Error";
          this.className= 'alert alert-danger';
      })
  }

}


<div class="container">
  <div class="row-justify-content-center">
    <div class="col-md-8">
      <div class="card shadow-lg p-4 mt-5 mb-6">
        <h1>Welcome here</h1><hr>
        <div [ngClass]="className" role="alert">
         {{message}} <a routerLink="/login" class="text-primary" style="cursor : pointer">Login here</a>

        </div>
        <form [formGroup] ="signupForm"  class="text-left">
        <div class="form-group">
          <label>
            Display name
          </label>
          <input type="text" class="form-control" placeholder="username" formControlName="displayName">
          <label>
            Email
          </label>
          <input type="email" class="form-control" placeholder="Email" formControlName="email">
          <label>
            Password
          </label>
          <input type="password" class="form-control" placeholder="**********" formControlName="password">
        </div><br>
        <button class="btn btn-outline-primary btn-block" (click)="signup()">Create Account</button>
      </form>
    </div>
  </div>









  <div class="container m-4">
    <h3 class="text-primary center">OTP Verification</h3>
    <div class="row form">
        <div class="col-auto">
            <input type="email" class="form-control email">
        </div>
        <div class="col-auto">
            <button class="btn btn-primary" onclick="sendOTP()">Send OTP</button>
        </div>
    </div>
    <div class="success text-success">
        OTP verified Success fully
    </div>

    <div class="verification">
        <div class="title center">
            <p>An OTP has been sent to <span class="emailpartial">chitraswami93@gmail.com</span></p>
        </div>
        <div class="otp-input-fields">
            <input type="number" class="otp_num otp_num_1" maxlength="1">
            <input type="number" class="otp_num otp_num_2" maxlength="1">
            <input type="number" class="otp_num otp_num_3" maxlength="1">
            <input type="number" class="otp_num otp_num_4" maxlength="1">
        </div>
    </div>
    <div class="error text-danger">
        Invalid otp
    </div>
</div>

<script>

    let emailEle = document.querySelector('.email');
    let verfEle = document.querySelector('.verification');
    let successEle = document.querySelector('.success');
    let errorEle = document.querySelector('.error');
    let otp_inputs = document.querySelectorAll('.otp_num');
    let emailpartialEle = document.querySelector('.emailpartial');
    let regex = new RegExp('[a-zA-Z0-9]+@[a-z]+\.[a-z]{2,3}');
    let otp_check = '';
    let email;

    otp_inputs.forEach(
        (ip) => {
            ip.addEventListener('keyup', moveNext)
        }
    )

    function moveNext(event) {
        // otp_num_4

        let current = event.target;
        let index = current.classList[1].slice(-1);
        if (event.keyCode == 8 && index > 1) {
            current.previousElementSibling.focus()
        }
        else if (index < 4) {
            current.nextElementSibling.focus()

        }
        otp_check = '';
        for (ip of otp_inputs) {
            otp_check += ip.value
        }
        if (otp_check.length == 4) {
            verifyOTP()
        }





    }

    function verifyOTP() {
        fetch('http://localhost:8080/verify',
            {
                method: "POST",
                body: JSON.stringify({
                    "email": `${email}`,
                    "otp": `${otp_check}`
                }),
                headers: { 'Content-Type': 'application/json' }


            }
        )
            .then(
                (res) => {
                    console.log(res)
                    if (res.status == 200) {
                        verfEle.style.display = 'none';
                        successEle.style.display = 'block';
                        errorEle.style.display = 'none';

                    }
                    else {
                        errorEle.style.display = 'block';
                        errorEle.innerHTML = "Invalid OTP";
                        successEle.style.display = 'none';

                    }
                }
            )

    }



    function sendOTP() {
        email = emailEle.value;
        if (regex.test(email)) {
            fetch('http://localhost:8080/sendotp', {
                method: "POST",
                body: JSON.stringify({
                    "email": `${email}`
                }),
                headers: { 'Content-Type': 'application/json' }
            })
                .then(
                    (res) => {
                        if (res.status == 200) {
                            verfEle.style.display = 'block';
                            emailpartialEle.value = "***" + email.slice(3)
                            emailEle.value = ''
                        }
                        else {
                            errorEle.style.display = 'block';
                            errorEle.innerHTML = "Email not exist";
                            successEle.style.display = 'none';

                        }
                    }
                )

        }
        else {
            errorEle.style.display = 'block';
            errorEle.innerHTML = "Invalid Email";
            successEle.style.display = 'none';

        }

    }

</script>

