import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { environment } from '../../environment/environment';

@Injectable({
  providedIn: 'root',
})
export class AuthService {

  address: any;
  constructor(private http: HttpClient) {}

  signup(data: any): Observable<any> {
    return this.http.post(`${environment.URL}/auth/register`, data);
  }

  signin(data: any): Observable<any> {
    return this.http.post(`${environment.URL}/auth/login`, data);
  }

  getProfile(): Observable<any> {
    const headers = {
      Authorization: 'Bearer ' + localStorage.getItem('token'),
    };
    return this.http.get(`${environment.URL}/auth/profile`, {
      headers: headers,
    });
  }
  // getUserId(): string {
  //   return 'some user id'; // replace this with actual implementation to get the user id
  // }

  // updateAddress(userId: string, address: any) {
  //   return this.http.put<any>(`${environment.URL}/users/${userId}/address`, address);
  // }

  updateAddress(address: any) {
    return this.http.put<any>(`${environment.URL}/auth/address`, address);
  }



}
