import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import {  environment  } from '../../environment/environment';


@Injectable({
  providedIn: 'root'
  // setting the field to ‘root’ makes the service available anywhere.
})
export class AuthService {

  constructor(private http:HttpClient) { }
  signup(data:any):Observable<any>{
    return this.http.post(`${environment.URL}/signup`, data);
  }

  signin(data:any):Observable<any>{
    return this.http.post(`${environment.URL}/signin`, data);
  }

  getProfile():Observable<any>{
    const headers ={

      'Authorization' : "Bearer " + localStorage.getItem('token')
    }
      return this.http.get(`${environment.URL}/auth/profile`, {headers:headers})
  }

}
