import { GoogleLoginProvider, SocialAuthService, SocialUser } from '@abacritt/angularx-social-login';
import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { AuthService } from 'src/app/service/auth.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit{
  loginForm!: FormGroup
  user!: SocialUser


  constructor (private fb:FormBuilder, private auth:AuthService, private router:Router, private authService:SocialAuthService ) {
    this.loginForm = this.fb.group({
      'email':['', Validators.required],
      'password':['', Validators.required]
    })
  }

  ngOnInit(): void {
    this.authService.authState.subscribe((user)=>{
      this.user = user;
    })
  }


  signWithGoogle(): any{
    this.authService.signIn(GoogleLoginProvider.PROVIDER_ID);
  }

  signOut(): any{
    this.authService.signOut();
  }
  login(){
    const data = this.loginForm.value;
    this.auth.signin(data).subscribe((res)=>{
      if(res.success){
        localStorage.setItem('token', res.token)
      this.router.navigate(['/profile'])
      }else{
        alert(res.message)
      }
    }, err=>{
      alert("Login Failed")
    })
  }

}


<div class="container mt-2">
  <div class="row-justify-content-center">
    <div class="col-md-8">
      <div class="card shadow-lg p-4 mt-5 mb-6">
        <h1>Welcome Back </h1><hr>
        <!-- <div class="alert alert-success" role="alert">
          Login Sucessfully

        </div> -->
        <form [formGroup] ="loginForm" class="text-left">
        <div class="form-group">

          <label>
            Email
          </label>
          <input type="email" class="form-control" placeholder="Email" formControlName="email">
          <label>
            Password
          </label>
          <input type="password" class="form-control" placeholder="**********" formControlName="password">
        </div><br>
        <button class="btn btn-outline-primary btn-block" (click)="signWithGoogle()">Login</button>
      </form>
    </div>
  </div>

</div>
