import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { AuthService } from 'src/app/service/auth.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit{
  loginForm!: FormGroup


  constructor (private fb:FormBuilder, private auth:AuthService ) {
    this.loginForm = this.fb.group({
      'email':['', Validators.required],
      'password':['', Validators.required]
    })
  }

  ngOnInit(): void {
  }
  login(){
    const data = this.loginForm.value;
    this.auth.signin(data).subscribe((res)=>{
      if(res.success){
      alert("Login Successful")}
    }, err=>{
      alert("Login Failed")
    })
  }

}


<div class="container mt-2">
  <div class="row-justify-content-center">
    <div class="col-md-8">
      <div class="card shadow-lg p-4 mt-5 mb-6">
        <h1>Welcome Back </h1><hr>
        <div class="alert alert-success" role="alert">
          Login Sucessfully

        </div>
        <form [formGroup] = "loginForm" class="text-left">
        <div class="form-group">

          <label>
            Email
          </label>
          <input type="email" class="form-control" placeholder="Email" formControlName="email">
          <label>
            Password
          </label>
          <input type="password" class="form-control" placeholder="**********" formControlName="password">
        </div><br>
        <button class="btn btn-outline-primary btn-block" (click)="login()">logged in!</button>
      </form>
    </div>
  </div>

</div>
